import React, { useState, useEffect } from "react";
import axios from "axios";

import PaymentOptions from "./payment";

const cells = Array.from({ length: 100 }, (_, index) => (
  <div key={index} className="bidlist-cell"></div>
));

let bidend = true;

function View2(props) {

  const [auctionResults, setAuctionResults] = useState([]);

  useEffect(() => {
    // Fetch auction results based on e_id and category from backend
    axios
      .post('http://localhost:5000/auction_result', {
        e_id: 1,
        category: 'Premium  ' // Change 'Regular' to whatever category you need
      })
      .then((response) => {
        setAuctionResults(response.data);
        console.log(auctionResults)
      })
      .catch((error) => {
        console.error('Error fetching auction results:', error);
      });
  }, []);

  return (
    <div class="bottom_view2">
      return (
    <div>
      <div className="bid">
        <div className="bidlist">
          <p>
            <span className="column">b_id</span>
            <span className="column">bid_amount</span>
            <span className="column">e_id</span>
            <span className="column">u_id</span>
            <span className="column">category</span>
          </p>
          {auctionResults.map((result, index) => (
            <p key={index}>
              <span className="column">{result.b_id}</span>
              <span className="column">{result.bid_amount}</span>
              <span className="column">{result.e_id}</span>
              <span className="column">{result.u_id}</span>
              <span className="column">{result.category}</span>
            </p>
          ))}
        </div>
      </div>
    </div>
  );
};

      <div class="buy">
        {bidend ? (
          <div>
            <h1>{props.bid}</h1>
            <h1>BUY TICKET</h1>
            <p>Choose your medium of payment.</p>
            <PaymentOptions />
          </div>
        ) : (
          <h1>
            Bidding period has not elapsed! Come back later or make a new bid.
          </h1>
        )}
      </div>
    </div>
  );
}

export default View2;
